          gap: 1rem;
          margin-bottom: 2rem;
          flex-wrap: wrap;
        }

        .search-box {
          flex: 1;
          position: relative;
          min-width: 300px;
        }

        .search-icon {
          position: absolute;
          left: 1rem;
          top: 50%;
          transform: translateY(-50%);
          color: #888;
          font-size: 1.2rem;
        }

        .search-input {
          width: 100%;
          padding: 1rem 1rem 1rem 3rem;
          background: #1a1a1a;
          border: 1px solid #333;
          border-radius: 8px;
          color: #fff;
          font-size: 1rem;
        }

        .search-input:focus {
          outline: none;
          border-color: #ff6b35;
          box-shadow: 0 0 10px rgba(255, 107, 53, 0.3);
        }

        .filters {
          display: flex;
          gap: 1rem;
        }

        .filter-select {
          padding: 1rem;
          background: #1a1a1a;
          border: 1px solid #333;
          border-radius: 8px;
          color: #fff;
          cursor: pointer;
        }

        .filter-select:focus {
          outline: none;
          border-color: #ff6b35;
        }

        .notes-stats {
          display: flex;
          gap: 2rem;
          margin-bottom: 2rem;
          padding: 1.5rem;
          background: linear-gradient(135deg, #1a1a1a 0%, #0d0d0d 100%);
          border: 1px solid #333;
          border-radius: 12px;
          flex-wrap: wrap;
        }

        .stat-item {
          text-align: center;
          flex: 1;
          min-width: 120px;
        }

        .stat-number {
          display: block;
          font-size: 2rem;
          font-weight: 700;
          color: #ff6b35;
          margin-bottom: 0.5rem;
        }

        .stat-label {
          color: #888;
          font-size: 0.9rem;
          text-transform: uppercase;
          letter-spacing: 1px;
        }

        .notes-grid {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
          gap: 1.5rem;
        }

        .note-card {
          background: linear-gradient(135deg, #1a1a1a 0%, #0d0d0d 100%);
          border: 1px solid #333;
          border-radius: 12px;
          padding: 1.5rem;
          transition: all 0.3s ease;
          position: relative;
          overflow: hidden;
        }

        .note-card::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          height: 3px;
          background: linear-gradient(90deg, #ff6b35 0%, #ff4757 100%);
          opacity: 0;
          transition: opacity 0.3s ease;
        }

        .note-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 30px rgba(255, 107, 53, 0.2);
          border-color: #ff6b35;
        }

        .note-card:hover::before {
          opacity: 1;
        }

        .note-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 1rem;
        }

        .note-type-badge {
          padding: 0.25rem 0.75rem;
          border-radius: 12px;
          font-size: 0.8rem;
          font-weight: 600;
          color: white;
          text-transform: uppercase;
          letter-spacing: 0.5px;
        }

        .note-actions {
          display: flex;
          gap: 0.5rem;
        }

        .action-btn {
          width: 28px;
          height: 28px;
          border: none;
          border-radius: 6px;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 0.8rem;
          transition: all 0.3s ease;
        }

        .view-btn {
          background: rgba(52, 152, 219, 0.2);
          color: #3498db;
        }

        .view-btn:hover {
          background: rgba(52, 152, 219, 0.3);
          transform: scale(1.1);
        }

        .edit-btn {
          background: rgba(255, 165, 2, 0.2);
          color: #ffa502;
        }

        .edit-btn:hover {
          background: rgba(255, 165, 2, 0.3);
          transform: scale(1.1);
        }

        .delete-btn {
          background: rgba(231, 76, 60, 0.2);
          color: #e74c3c;
        }

        .delete-btn:hover {
          background: rgba(231, 76, 60, 0.3);
          transform: scale(1.1);
        }

        .note-content {
          text-align: center;
          margin-bottom: 1.5rem;
        }

        .note-icon {
          font-size: 3rem;
          margin-bottom: 1rem;
          color: #ff6b35;
        }

        .note-title {
          color: #fff;
          font-size: 1.3rem;
          font-weight: 600;
          margin-bottom: 0.5rem;
          line-height: 1.3;
        }

        .note-subject {
          color: #ff6b35;
          font-weight: 500;
          margin-bottom: 0.75rem;
          text-transform: uppercase;
          letter-spacing: 1px;
          font-size: 0.9rem;
        }

        .note-description {
          color: #888;
          line-height: 1.5;
          font-size: 0.9rem;
        }

        .note-meta {
          display: flex;
          justify-content: space-between;
          margin-bottom: 1rem;
          padding: 1rem;
          background: rgba(255, 107, 53, 0.05);
          border-radius: 8px;
          border: 1px solid rgba(255, 107, 53, 0.1);
        }

        .meta-item {
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 0.25rem;
          flex: 1;
        }

        .meta-icon {
          font-size: 1.2rem;
        }

        .meta-value {
          color: #ccc;
          font-size: 0.8rem;
          font-weight: 500;
        }

        .note-footer {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding-top: 1rem;
          border-top: 1px solid #333;
          margin-bottom: 1rem;
        }

        .note-date, .note-size {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          color: #888;
          font-size: 0.8rem;
        }

        .date-icon, .size-icon {
          font-size: 1rem;
        }

        .note-preview {
          text-align: center;
        }

        .preview-link {
          display: inline-flex;
          align-items: center;
          gap: 0.5rem;
          padding: 0.5rem 1rem;
          background: linear-gradient(90deg, #ff6b35 0%, #ff4757 100%);
          color: white;
          text-decoration: none;
          border-radius: 6px;
          font-size: 0.9rem;
          font-weight: 500;
          transition: all 0.3s ease;
        }

        .preview-link:hover {
          transform: translateY(-2px);
          box-shadow: 0 5px 15px rgba(255, 107, 53, 0.4);
        }

        .preview-icon {
          font-size: 1rem;
        }

        .no-notes {
          grid-column: 1 / -1;
          text-align: center;
          padding: 4rem 2rem;
          color: #888;
        }

        .no-notes-icon {
          font-size: 4rem;
          margin-bottom: 1rem;
        }

        .no-notes h3 {
          color: #fff;
          margin-bottom: 1rem;
        }

        .no-notes .btn {
          margin-top: 1rem;
          display: inline-flex;
          align-items: center;
          gap: 0.5rem;
        }

        .approve-btn {
          background: rgba(46, 204, 113, 0.2);
          color: #2ecc71;
        }

        .approve-btn:hover {
          background: rgba(46, 204, 113, 0.3);
          transform: scale(1.1);
        }

        .reject-btn {
          background: rgba(231, 76, 60, 0.2);
          color: #e74c3c;
        }

        .reject-btn:hover {
          background: rgba(231, 76, 60, 0.3);
          transform: scale(1.1);
        }

        /* Preview Modal Styles */
        .preview-modal {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
