import React, { useState, useEffect } from 'react';

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('createdAt');
  const [filterBy, setFilterBy] = useState('all');
  const [previewNote, setPreviewNote] = useState(null);
  const [showPreview, setShowPreview] = useState(false);

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const response = await fetch('http://localhost:3001/admin/notes', {
        headers: {
          'x-api-key': 'masterstudent_admin_2024_secure_key'
        }
      });
      const data = await response.json();
      if (data.success) {
        setNotes(data.notes);
      }
    } catch (error) {
      console.error('Error fetching notes:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredNotes = notes.filter(note => {
    const matchesSearch = note.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         note.subject?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         note.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         note.id.toLowerCase().includes(searchTerm.toLowerCase());

    if (filterBy === 'all') return matchesSearch;
    if (filterBy === 'free') return matchesSearch && (note.price === 0 || !note.price);
    if (filterBy === 'paid') return matchesSearch && note.price > 0;
    if (filterBy === 'premium') return matchesSearch && note.isPremium;

    return matchesSearch;
  });

  const sortedNotes = [...filteredNotes].sort((a, b) => {
    if (sortBy === 'createdAt') {
      const dateA = a.createdAt?.seconds ? new Date(a.createdAt.seconds * 1000) : new Date(a.createdAt || 0);
      const dateB = b.createdAt?.seconds ? new Date(b.createdAt.seconds * 1000) : new Date(b.createdAt || 0);
      return dateB - dateA;
    }
    if (sortBy === 'title') return (a.title || '').localeCompare(b.title || '');
    if (sortBy === 'price') return (b.price || 0) - (a.price || 0);
    if (sortBy === 'downloads') return (b.downloads || 0) - (a.downloads || 0);
    return 0;
  });

  const formatDate = (timestamp) => {
    if (!timestamp) return 'Unknown';
    const date = timestamp.seconds ? new Date(timestamp.seconds * 1000) : new Date(timestamp);
    return date.toLocaleDateString();
  };

  const formatPrice = (price) => {
    if (!price || price === 0) return 'Free';
    return `â‚¹${price}`;
  };

  const getNoteType = (note) => {
    if (note.isPremium) return 'Premium';
    if (note.price > 0) return 'Paid';
    return 'Free';
  };

  const getTypeColor = (type) => {
    switch (type) {
      case 'Premium': return '#ff6b35';
      case 'Paid': return '#ffa502';
      case 'Free': return '#2ecc71';
      default: return '#888';
    }
  };

  const handlePreviewNote = (note) => {
    setPreviewNote(note);
    setShowPreview(true);
  };

  const handleApproveNote = async (noteId) => {
    try {
      const response = await fetch(`http://localhost:3001/admin/notes/${noteId}/approve`, {
        method: 'POST',
        headers: {
          'x-api-key': 'masterstudent_admin_2024_secure_key',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          adminEmail: 'admin@masterstudent.com'
        })
      });

      if (response.ok) {
        // Refresh notes list
        fetchNotes();
        alert('Note approved successfully!');
      } else {
        alert('Failed to approve note');
      }
    } catch (error) {
      console.error('Error approving note:', error);
      alert('Error approving note');
    }
  };

  const handleRejectNote = async (noteId) => {
    const reason = prompt('Please provide a reason for rejection:');
    if (!reason) return;

    try {
      const response = await fetch(`http://localhost:3001/admin/notes/${noteId}/reject`, {
        method: 'POST',
        headers: {
          'x-api-key': 'masterstudent_admin_2024_secure_key',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          adminEmail: 'admin@masterstudent.com',
          reason: reason
        })
      });

      if (response.ok) {
        // Refresh notes list
        fetchNotes();
        alert('Note rejected successfully!');
      } else {
        alert('Failed to reject note');
      }
    } catch (error) {
      console.error('Error rejecting note:', error);
      alert('Error rejecting note');
    }
  };

  const downloadFile = async (filePath, fileName) => {
    try {
      const response = await fetch(`http://localhost:8000${filePath}`);
      const blob = await response.blob();

      // Create download link
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = fileName || 'download';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error downloading file:', error);
      alert('Failed to download file');
    }
  };

  const getFileTypePreview = (fileName, filePath) => {
    if (!fileName) return null;

    const extension = fileName.split('.').pop().toLowerCase();
    const fileUrl = `http://localhost:8000${filePath}`;

    switch (extension) {
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif':
        return (
          <div className="image-preview">
            <img 
              src={fileUrl} 
              alt="Preview" 
              style={{ maxWidth: '100%', maxHeight: '200px', borderRadius: '8px' }}
              onError={(e) => {
                e.target.style.display = 'none';
              }}
            />
          </div>
        );

      case 'pdf':
        return (
          <div className="pdf-preview">
            <iframe 
              src={fileUrl}
              width="100%" 
              height="300px"
              style={{ border: '1px solid #333', borderRadius: '8px' }}
              title="PDF Preview"
            />
          </div>
        );

      default:
        return (
          <div className="file-info-preview">
            <div className="file-details">
              <span className="file-extension">{extension.toUpperCase()}</span>
              <span className="file-name">{fileName}</span>
            </div>
          </div>
        );
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading notes...</p>
      </div>
    );
  }

  return (
    <div className="notes-page fade-in-up">
      <div className="page-header">
        <h1 className="page-title">ðŸ“š Notes Management</h1>
        <p className="page-subtitle">Manage all uploaded notes from Firebase & Cloudinary</p>
      </div>

      <div className="notes-controls">
        <div className="search-box">
          <span className="search-icon">ðŸ”</span>
          <input
            type="text"
            placeholder="Search notes by title, subject, or description..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>

        <div className="filters">
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="filter-select"
          >
            <option value="createdAt">Sort by Date</option>
            <option value="title">Sort by Title</option>
            <option value="price">Sort by Price</option>
            <option value="downloads">Sort by Downloads</option>
          </select>

          <select
            value={filterBy}
            onChange={(e) => setFilterBy(e.target.value)}
            className="filter-select"
          >
            <option value="all">All Notes</option>
            <option value="free">Free Notes</option>
            <option value="paid">Paid Notes</option>
            <option value="premium">Premium Notes</option>
          </select>
        </div>
      </div>

      <div className="notes-stats">
        <div className="stat-item">
          <span className="stat-number">{notes.length}</span>
          <span className="stat-label">Total Notes</span>
        </div>
        <div className="stat-item">
          <span className="stat-number">{notes.filter(n => n.price > 0).length}</span>
          <span className="stat-label">Paid Notes</span>
        </div>
        <div className="stat-item">
          <span className="stat-number">{notes.filter(n => n.isPremium).length}</span>
          <span className="stat-label">Premium Notes</span>
        </div>
        <div className="stat-item">
          <span className="stat-number">{filteredNotes.length}</span>
          <span className="stat-label">Filtered Results</span>
        </div>
      </div>

      <div className="notes-grid">
        {sortedNotes.length > 0 ? (
          sortedNotes.map((note) => (
            <div key={note.id} className="note-card">
              <div className="note-header">
                <div className="note-type-badge" style={{ backgroundColor: getTypeColor(getNoteType(note)) }}>
                  {getNoteType(note)}
                </div>
                <div className="note-actions">
                  <button 
                    className="action-btn view-btn" 
                    title="Preview Note"
                    onClick={() => handlePreviewNote(note)}
                  >
                    ðŸ‘ï¸
                  </button>
                  {note.approvalStatus === 'pending' && (
                    <>
                      <button 
                        className="action-btn approve-btn" 
                        title="Approve Note"
                        onClick={() => handleApproveNote(note.id)}
                      >
                        âœ…
                      </button>
                      <button 
                        className="action-btn reject-btn" 
                        title="Reject Note"
                        onClick={() => handleRejectNote(note.id)}
                      >
                        âŒ
                      </button>
                    </>
                  )}
                  <button className="action-btn delete-btn" title="Delete Note">
                    ðŸ—‘ï¸
                  </button>
                </div>
              </div>

              <div className="note-content">
                <div className="note-icon">ðŸ“„</div>
                <h3 className="note-title">{note.title || 'Untitled Note'}</h3>
                <p className="note-subject">{note.subject || 'No subject'}</p>
                <p className="note-description">
                  {note.description ? 
                    (note.description.length > 100 ? 
                      note.description.substring(0, 100) + '...' : 
                      note.description
                    ) : 
                    'No description available'
                  }
                </p>
              </div>

              <div className="note-meta">
                <div className="meta-item">
                  <span className="meta-icon">ðŸ’°</span>
                  <span className="meta-value">{formatPrice(note.price)}</span>
                </div>
                <div className="meta-item">
                  <span className="meta-icon">ðŸ“¥</span>
                  <span className="meta-value">{note.downloads || 0} downloads</span>
                </div>
                <div className="meta-item">
                  <span className="meta-icon">â­</span>
                  <span className="meta-value">{note.rating || 0}/5</span>
                </div>
              </div>

              <div className="note-footer">
                <div className="note-date">
                  <span className="date-icon">ðŸ“…</span>
                  <span>Uploaded {formatDate(note.createdAt)}</span>
                </div>
                <div className="note-size">
                  <span className="size-icon">ðŸ“Š</span>
                  <span>{note.fileSize || 'Unknown size'}</span>
                </div>
              </div>

              {note.cloudinaryUrl && (
                <div className="note-preview">
                  <a 
                    href={note.cloudinaryUrl} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="preview-link"
                  >
                    <span className="preview-icon">â˜ï¸</span>
                    View on Cloudinary
                  </a>
                </div>
              )}
            </div>
          ))
        ) : (
          <div className="no-notes">
            <div className="no-notes-icon">ðŸ“š</div>
            <h3>No Notes Found</h3>
            <p>No notes match your current search and filter criteria.</p>
            <button className="btn btn-primary">
              <span>ðŸ“¤</span>
              Upload First Note
            </button>
          </div>
        )}
      </div>

      {/* Note Preview Modal */}
      {showPreview && previewNote && (
        <div className="preview-modal">
          <div className="preview-content">
            <div className="preview-header">
              <h2>ðŸ“„ Note Preview</h2>
              <button 
                className="close-btn"
                onClick={() => setShowPreview(false)}
              >
                âœ•
              </button>
            </div>

            <div className="preview-body">
              <div className="preview-info">
                <div className="info-row">
                  <span className="info-label">Title:</span>
                  <span className="info-value">{previewNote.title}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Subject:</span>
                  <span className="info-value">{previewNote.subject}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Category:</span>
                  <span className="info-value">{previewNote.category}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Uploaded by:</span>
                  <span className="info-value">{previewNote.userEmail}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Status:</span>
                  <span className={`status-badge ${previewNote.approvalStatus}`}>
                    {previewNote.approvalStatus || 'pending'}
                  </span>
                </div>
                <div className="info-row">
                  <span className="info-label">File Size:</span>
                  <span className="info-value">{previewNote.fileSize || 'Unknown'}</span>
                </div>
              </div>

              <div className="preview-description">
                <h4>Description:</h4>
                <p>{previewNote.description || 'No description provided'}</p>
              </div>

              <div className="preview-file">
                <h4>File Preview:</h4>
                {previewNote.cloudinaryUrl ? (
                  <div className="file-preview-container">
                    <a 
                      href={previewNote.cloudinaryUrl} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="file-link"
                    >
                      <span className="file-icon">â˜ï¸</span>
                      View on Cloudinary
                    </a>
                  </div>
                ) : previewNote.filePath ? (
                  <div className="file-preview-container">
                    <a 
                      href={`http://localhost:8000${previewNote.filePath}`}
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="file-link"
                    >
                      <span className="file-icon">ðŸ“</span>
                      View File
                    </a>
                    <button 
                      className="download-btn"
                      onClick={() => downloadFile(previewNote.filePath, previewNote.fileName)}
                    >
                      <span className="download-icon">â¬‡ï¸</span>
                      Download
                    </button>
                  </div>
                ) : (
                  <div className="no-file">
                    <span className="no-file-icon">ðŸ“„</span>
                    <p>File not available for preview</p>
                  </div>
                )}

                {/* File Type Preview */}
                {previewNote.filePath && (
                  <div className="file-type-preview">
                    {getFileTypePreview(previewNote.fileName, previewNote.filePath)}
                  </div>
                )}
              </div>

              {previewNote.approvalStatus === 'pending' && (
                <div className="preview-actions">
                  <button 
                    className="btn btn-success"
                    onClick={() => {
                      handleApproveNote(previewNote.id);
                      setShowPreview(false);
                    }}
                  >
                    âœ… Approve Note
                  </button>
                  <button 
                    className="btn btn-danger"
                    onClick={() => {
                      handleRejectNote(previewNote.id);
                      setShowPreview(false);
                    }}
                  >
                    âŒ Reject Note
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      <style jsx>{`
        .notes-controls {
          display: flex;
          gap: 1rem;
          margin-bottom: 2rem;
          flex-wrap: wrap;
        }

        .search-box {
          flex: 1;
          position: relative;
          min-width: 300px;
        }

        .search-icon {
          position: absolute;
          left: 1rem;
          top: 50%;
          transform: translateY(-50%);
          color: #888;
          font-size: 1.2rem;
        }

        .search-input {
          width: 100%;
          padding: 1rem 1rem 1rem 3rem;
          background: #1a1a1a;
          border: 1px solid #333;
          border-radius: 8px;
          color: #fff;
          font-size: 1rem;
        }

        .search-input:focus {
          outline: none;
          border-color: #ff6b35;
          box-shadow: 0 0 10px rgba(255, 107, 53, 0.3);
        }

        .filters {
          display: flex;
          gap: 1rem;
        }

        .filter-select {
          padding: 1rem;
          background: #1a1a1a;
          border: 1px solid #333;
          border-radius: 8px;
          color: #fff;
          cursor: pointer;
        }

        .filter-select:focus {
          outline: none;
          border-color: #ff6b35;
        }

        .notes-stats {
          display: flex;
          gap: 2rem;
          margin-bottom: 2rem;
          padding: 1.5rem;
          background: linear-gradient(135deg, #1a1a1a 0%, #0d0d0d 100%);
          border: 1px solid #333;
          border-radius: 12px;
          flex-wrap: wrap;
        }

        .stat-item {
          text-align: center;
          flex: 1;
          min-width: 120px;
        }

        .stat-number {
          display: block;
          font-size: 2rem;
          font-weight: 700;
          color: #ff6b35;
          margin-bottom: 0.5rem;
        }

        .stat-label {
          color: #888;
          font-size: 0.9rem;
          text-transform: uppercase;
          letter-spacing: 1px;
        }

        .notes-grid {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
          gap: 1.5rem;
        }

        .note-card {
          background: linear-gradient(135deg, #1a1a1a 0%, #0d0d0d 100%);
          border: 1px solid #333;
          border-radius: 12px;
          padding: 1.5rem;
          transition: all 0.3s ease;
          position: relative;
          overflow: hidden;
        }

        .note-card::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          height: 3px;
          background: linear-gradient(90deg, #ff6b35 0%, #ff4757 100%);
          opacity: 0;
          transition: opacity 0.3s ease;
        }

        .note-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 30px rgba(255, 107, 53, 0.2);
          border-color: #ff6b35;
        }

        .note-card:hover::before {
          opacity: 1;
        }

        .note-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 1rem;
        }

        .note-type-badge {
          padding: 0.25rem 0.75rem;
          border-radius: 12px;
          font-size: 0.8rem;
          font-weight: 600;
          color: white;
          text-transform: uppercase;
          letter-spacing: 0.5px;
        }

        .note-actions {
          display: flex;
          gap: 0.5rem;
        }

        .action-btn {
          width: 28px;
          height: 28px;
          border: none;
          border-radius: 6px;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 0.8rem;
          transition: all 0.3s ease;
        }

        .view-btn {
          background: rgba(52, 152, 219, 0.2);
          color: #3498db;
        }

        .view-btn:hover {
          background: rgba(52, 152, 219, 0.3);
          transform: scale(1.1);
        }

        .edit-btn {
          background: rgba(255, 165, 2, 0.2);
          color: #ffa502;
        }

        .edit-btn:hover {
          background: rgba(255, 165, 2, 0.3);
          transform: scale(1.1);
        }

        .delete-btn {
          background: rgba(231, 76, 60, 0.2);
          color: #e74c3c;
        }

        .delete-btn:hover {
          background: rgba(231, 76, 60, 0.3);
          transform: scale(1.1);
        }

        .note-content {
          text-align: center;
          margin-bottom: 1.5rem;
        }

        .note-icon {
          font-size: 3rem;
          margin-bottom: 1rem;
          color: #ff6b35;
        }

        .note-title {
          color: #fff;
          font-size: 1.3rem;
          font-weight: 600;
          margin-bottom: 0.5rem;
          line-height: 1.3;
        }

        .note-subject {
          color: #ff6b35;
          font-weight: 500;
          margin-bottom: 0.75rem;
          text-transform: uppercase;
          letter-spacing: 1px;
          font-size: 0.9rem;
        }

        .note-description {
          color: #888;
          line-height: 1.5;
