    const extension = fileName.split('.').pop().toLowerCase();
    switch (extension) {
      case 'pdf': return 'ðŸ“„';
      case 'doc':
      case 'docx': return 'ðŸ“';
      case 'ppt':
      case 'pptx': return 'ðŸ“Š';
      case 'xls':
      case 'xlsx': return 'ðŸ“ˆ';
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif': return 'ðŸ–¼ï¸';
      case 'mp4':
      case 'avi':
      case 'mov': return 'ðŸŽ¥';
      case 'mp3':
      case 'wav': return 'ðŸŽµ';
      case 'zip':
      case 'rar': return 'ðŸ“¦';
      default: return 'ðŸ“';
    }
  };

