        @media (max-width: 768px) {
          .upload-stats {
            flex-direction: column;
            align-items: center;
          }

          .upload-area {
            padding: 2rem 1rem;
          }

          .upload-actions {
            flex-direction: column;
          }

          .result-actions {
            flex-direction: column;
          }

          .tips-grid {
            grid-template-columns: 1fr;
          }
        }
      `}</style>
    </div>
  );
};

export default Upload;
